import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import axios from "axios";

const ReadBlog = () => {
    const { _id } = useParams();
    const [blogData, setBlogData] = useState({ title: '', description: '', imageData: '' });

    useEffect(() => {
        const getData = async () => {
            try {
                const response = await axios.get(`http://localhost:4000/api/blogs/blogread/?_id=${_id}`);
                const { data, contentType, title, description } = response.data;

                // Convert binary image data to Base64 string
                const imageData = Buffer.from(data, "binary").toString("base64");

                setBlogData({ title, description, imageData, contentType });
            } catch (error) {
                console.error("Error fetching blog data:", error);
            }
        };

        getData(); 
    }, [_id]);

    return (
        <div className="container-fluid">
            <div className="row m-2">
                <div className="col-sm-2">
                    <button className="btn btn-dark">
                        <Link to="/Blog" id="lnk" style={{ color: "white", textDecoration: "none" }}>
                            <ChevronLeftIcon /> Back
                        </Link>
                    </button>
                </div>
                <div className="col-sm-10">
                    <h2>Title:</h2>
                    <h3>{blogData.title}</h3>
                </div>
            </div>
            <hr />
            {/* Image and Description */}
            <div className="row">
                <div className="col-sm-4">
                    <div className="card mb-3">
                    {blogData.imageData && (
        <img
          src={`data:${blogData.contentType};base64,${blogData.imageData}`}
          alt={blogData.title}
          style={{ maxWidth: "100%", maxHeight: "400px" }}
        />
      )}

                    </div>
                </div>
                <div className="col-sm-8">
                    <h5>Description:</h5><br />
                    <p>{blogData.description}</p>
                </div>
            </div>
        </div>
    );
};

export default ReadBlog;
