import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Sidebar from "../jsx/Sidebar";
import "../css/Dashboard.css";
import tetime from "../images/Rich Startup.png";
import Footer from "../jsx/Footer";
import axiosInstance from "../Axios/axios";

const Dashboard = () => {
  const [blogData, setBlogData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axiosInstance.get("/api/blogs/readblog");
        console.log("Response:", response.data);
        setBlogData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); 
  }, []); 
  const convertImageDataToBase64 = (imageData) => {
    return `data:${imageData.contentType};base64,${Buffer.from(imageData.data).toString('base64')}`;
  };

  return (
    <div className="container-fluid" id="dash">
      <div className="row">
        <Sidebar />
        <div className="col-sm-10">
          <div id="bdtxt">
            <div className="d-flex justify-content-center align-items-center" style={{ perspective: "1000px" }}>
              <img src={tetime} alt="Tetime" className="rotating-image" />
            </div>
            <h1 className="d-flex justify-content-center text-center">Welcome to</h1>
            <h1 className="text-danger text-center">Tetime</h1>
            <p className="text-center">Invest time and technology with your blogging skills</p>
          </div>
        </div>
      </div>
      <div className="row bg-light">
        <h3 className="text-center text-danger">Explore Blogs</h3>
        {blogData && blogData.length > 0 ? (
          blogData.map(blog => (
            <div key={blog._id} className="mb-3">
              <table style={{ border: "solid" }}>
                <tbody>
                  <tr>
                    <td style={{ border: "solid", maxWidth: "200px", overflow: "hidden" }}>
                      <h6 style={{ whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", margin: "0", padding: "5px" }}>
                        Title: {blog.title}
                      </h6>
                    </td>
                    <td style={{ border: "solid", maxWidth: "30vw", overflow: "hidden" }}>
                      <h6 style={{ whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", margin: "0", padding: "5px" }}>
                        Description: {blog.description}
                      </h6>
                    </td>
                    <td>
                      <img src={convertImageDataToBase64(blog.image)} alt={blog.title} style={{ width: "auto", height: "auto", maxWidth: "200px" }} />
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          ))
        ) : (
          <p>No blogs available</p>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default Dashboard;
