import React, { useState, useEffect } from "react";
import Sidebar from "../jsx/Sidebar";
import { Link } from "react-router-dom";
import axios from "axios";
import Footer from "../jsx/Footer";

const Blog = () => {
  const [blogData, setBlogData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const user_id = sessionStorage.getItem("user_id");
        const response = await axios.get("http://localhost:4000/api/blogs/readblog",
        );
        setBlogData(response.data); // Assuming response.data is an array of blog objects
      } catch (error) {
        console.error("Error fetching user blogs:", error);
      }
    };

    fetchData();
  }, []);

  const columns = [
    { field: "title", headerName: "Title" },
    { field: "discription", headerName: "Description" },
  ];

  return (
    <>
      <div className="container-fluid">
        <div className="row">
          <Sidebar />
          <div className="col-sm-10">
            <h1 className="text-danger text-center">Tetime</h1>
            <h3 className="text-dark text-center">Blog of the Time</h3>
            <Link to="/createblog" id="lnk">
              <button className="btn btn-success m-2">Add Blog</button>
            </Link>
            <table className="table">
              <thead>
                <tr>
                  {columns.map((column) => (
                    <th key={column.field}>{column.headerName}</th>
                  ))}
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {blogData.map((blog) => (
                  <tr key={blog._id}>
                    {columns.map((column) => (
                      <td key={`${blog._id}-${column.field}`}>{blog[column.field]}</td>
                    ))}
                    <td>
                      <div className="m-2">
                        <Link to={`/readblog/${blog._id}`} id="lnk">
                          <button className="btn btn-dark m-2">Read</button>
                        </Link>
                        <button className="btn btn-success m-2">Update</button>
                        <button className="btn btn-danger m-2">Remove</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Blog;
