import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import blogimage from '../images/blogimg.png';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';

const ReadBlog = () => {
  const { blogId } = useParams(); // Correctly extract blogId from URL params
  const [blogData, setBlogData] = useState({ title: '', discription: '' });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/blogs/blogread/?_id=${blogId}`);
        const { title, discription } = response.data;
        setBlogData({ title, discription });
      } catch (error) {
        console.error("Error fetching blog data:", error);
      }
    };

    fetchData();
  }, [blogId]); // Fetch data whenever blogId changes

  return (
    <div className="container-fluid">
      <div className="row m-2">
        <div className="col-sm-2">
          <button className="btn btn-dark">
            <Link to="/Blog" id="lnk" style={{ color: "white", textDecoration: "none" }}>
              <ChevronLeftIcon /> Back
            </Link>
          </button>
        </div>
        <div className="col-sm-10">
          <h2>Title:</h2>
          <h3>{blogData.title}</h3>
        </div>
      </div>
      <hr />
      {/* Image and description */}
      <div className="row">
        <div className="col-sm-4">
          <img src={blogimage} style={{ height: "150px" }} alt="Blog" />
        </div>
        <div className="col-sm-8">
          <h5>Description:</h5>
          <p>{blogData.description}</p>
        </div>
      </div>
    </div>
  );
};

export default ReadBlog;
