import React, { useState } from "react";
import axiosInstance from '../Axios/axios';
import { Link } from "react-router-dom";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";

const NewBlog = () => {
  const user_id = sessionStorage.getItem("user_id");

  const [blogData, setBlogData] = useState({
    title: "",
    image: null, // Use 'image' instead of 'file' for clarity
    description: "",
    user_id: user_id,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setBlogData((prevData) => ({
      ...prevData,
      [name]: name === "image" ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("title", blogData.title);
      formData.append("description", blogData.description);
      formData.append("image", blogData.image);
      formData.append("user_id", blogData.user_id);

      const response = await axiosInstance.post("/api/blogs/writeblog", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      console.log("Response:", response.data);
      alert("Blog added successfully!");

      setBlogData({
        title: "",
        image: null,
        description: "",
        user_id: user_id,
      });
    } catch (error) {
      console.error("Error adding blog:", error);
      alert("Something went wrong!");
    }
  };

  return (
    <div className="container-fluid">
      <button className="btn btn-dark mt-2">
        <Link to="/Blog" style={{ color: "white", textDecoration: "none" }} id="lnk">
          <ChevronLeftIcon />
          Back
        </Link>
      </button>
      <h1 className="text-danger text-center">Tetime</h1>
      <div className="row pt-2">
        <h1 id="blog">Create new Blog post</h1>
        <div className="col-sm-12">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                className="form-control"
                id="title"
                name="title"
                placeholder="Enter title"
                value={blogData.title}
                onChange={handleChange}
              />
            </div>
            <div className="form-group mt-2">
              <label>Add Image</label>
              <br />
              <input
                type="file"
                id="image" // Use 'image' as ID for the file input
                name="image"
                onChange={handleChange}
              />
            </div>
            <div className="form-group mt-2">
              <label htmlFor="description">Description</label>
              <textarea
                type="text"
                className="form-control"
                id="description"
                name="description"
                placeholder="Description"
                style={{ height: "200px" }}
                value={blogData.description}
                onChange={handleChange}
              />
            </div>
            <button type="submit" className="btn btn-success mt-2">
              Add Blog
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewBlog;
