import React, { useEffect, useState } from "react";
import Sidebar from "../jsx/Sidebar";
import { Link } from "react-router-dom";
import axios from "axios";
import Footer from "../jsx/Footer";
import { Button, Modal, Form, Col } from "react-bootstrap";

const columns = [
  { field: "title", headerName: "Title" }
];

const Blog = () => {
  const [userBlogs, setUserBlogs] = useState([]);
  const [show, setShow] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    role: ""
  });

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    const fetchUserBlogs = async () => {
      try {
        const user_id = sessionStorage.getItem("user_id");
        const response = await axios.get(`http://localhost:4000/api/blogs/userblog?user_id=${user_id}`);

        if (response.data) {
          setUserBlogs(response.data);
        }
      } catch (error) {
        console.error("Error fetching user blogs:", error);
      }
    };

    fetchUserBlogs();
  }, []);

  const handleDelete = async (_id) => {
    try {
      await axios.delete(`http://localhost:4000/api/blogs/deleteblog?_id=${_id}`);
      setUserBlogs(userBlogs.filter((blog) => blog._id !== _id));
      console.log("Blog deleted successfully");
    } catch (error) {
      console.error("Error deleting blog:", error);
    }
  };

  const handleUpdate = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async () => {
    try {
      // Send updated form data to the server (e.g., via axios POST request)
      console.log("Updated form data:", formData);

      // Close the modal after form submission
      setShow(false);
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <Sidebar />
        <div className="col-sm-10">
          <h1 className="text-danger text-center">Tetime</h1>
          <h3 className="text-dark text-center">Blog of the Time</h3>
          <Link to="/createblog" id="lnk">
            <button className="btn btn-success m-2">Add Blog</button>
          </Link>
          <table className="table">
            <thead>
              <tr>
                {columns.map((column) => (
                  <th key={column.field} style={{ width: "60%" }}>{column.headerName}</th>
                ))}
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {userBlogs.map((blog) => (
                <tr key={blog._id}>
                  {columns.map((column) => (
                    <td key={`${blog._id}-${column.field}`}>{blog[column.field]}</td>
                  ))}
                  <td>
                    <Link to={`/ReadBlog/${blog._id}`} className="btn btn-dark m-2">
                      Preview
                    </Link>
                    <button className="btn btn-success m-2" onClick={handleShow}>
                      Update
                    </button>
                    <button className="btn btn-danger m-2" onClick={() => handleDelete(blog._id)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <Footer />

      {/* Modal */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton className="modalTitle">
          <Modal.Title>Update Form</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-2" controlId="formGridAddress1">
              <Form.Label className="modalLabel">Name</Form.Label>
              <Form.Control
                placeholder="Name"
                name="name"
                value={formData.name}
                onChange={handleUpdate}
              />
            </Form.Group>
            <Form.Group controlId="formGridEmail">
              <Form.Label className="modalLabel">Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Email"
                name="email"
                value={formData.email}
                onChange={handleUpdate}
              />
            </Form.Group>
            <Form.Group as={Col} className="mb-2" controlId="formGridState">
              <Form.Label className="modalLabel">Role</Form.Label>
              <Form.Select
                aria-label="Default select example"
                name="role"
                value={formData.role}
                onChange={handleUpdate}
              >
                <option disabled selected value={""}>
                  Select Role
                </option>
                <option value="admin">Admin</option>
                <option value="user">User</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Blog;
