import React, { useEffect, useState } from "react";
import Sidebar from "../jsx/Sidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import "../css/Blog.css";
import axios from "axios";
import Footer from "../jsx/Footer";

// import { useLocation } from 'react-router-dom';

const columns = [
  { field: "title", headerName: "Title" },
  { field: "discription", headerName: "Discription" },
];

const Blog = () => {
  const [abc, setAbc] = useState([
    {
      title: "",
      discription: "",
    },
  ]);
  const [userBlogs, setUserBlogs] = useState([]);

  useEffect(() => {
    const fetchUserBlogs = async () => {
      try {
        const user_id = sessionStorage.getItem("user_id");
        const response = await axios.get(
          `http://localhost:4000/api/blogs/userblog?user_id=${user_id}`
        );

        if (response.data) {
          setUserBlogs(response.data);
        }
      } catch (error) {
        console.error("Error fetching user blogs:", error);
      }
    };

    fetchUserBlogs();
  }, []);

  //  const rows = [
  //   {
  //     title: title,
  //     discription:discription
  //   }
  // ];

  return (
    <>
      <div className="container-fluid">
        <div className="row ">
          <Sidebar />
          <div className="col-sm-10 ">
            <h1 className="text-danger text-center">Tetime</h1>
            <h3 className="text-dark text-center">Blog of the Time</h3>
            <Link to="/createblog" id="lnk">
              <button className="btn btn-success m-2">Add Blog</button>
            </Link>
            <table className="table">
              <thead>
                <tr>
                  {columns.map((column) => (
                    <th key={column.field}>{column.headerName}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {
                  <ul>
                    {userBlogs.map((blog) => (
                      <li key={blog._id}>
                        <h3>{blog.title}</h3>
                        <p>{blog.discription}</p>
                      </li>
                    ))}
                  </ul>
                }
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Blog;
